import React from "react";
import { connect } from "react-redux";

import Modal from "../common/Modal";
import history from "../../history";
import { deleteBoard } from "../../actions";

const BoardsDelete = (props) => {
  const renderActions = () => {
    return (
      <React.Fragment>
        <button
          className="btn btn-danger"
          onClick={() => props.deleteBoard(props.match.paarams.id)}
        >
          Delete
        </button>
        <button
          className="btn btn-secondary"
          onClick={() => history.push("/boards")}
        >
          Cancel
        </button>
      </React.Fragment>
    );
  };

  const renderBody = (item) => {
    const message = "Are you sure you want to delete this item";
    if (item)
      return `${message} with Company: ${item.company} and stock: ${item.stock}`;
    else return `${message}`;
  };

  return (
    <Modal
      title=""
      body={renderBody(props.item)}
      actions={renderActions()}
      onDismiss={() => history.push("/boards")}
    />
  );
};

export default conenct(null, { deleteBoard })(BoardsDelete);
