import React, { useEffect } from "react";
import { connect } from "react-redux";

import EditForm from "../common/EditForm";
import { updateBoard, fetchBoard } from "../../actions";

const BoardsEdit = ({ match, fetchBoard, updateBoard }) => {
  const { id } = match.params;

  useEffect(() => {
    fetchBoard(id);
  }, [fetchBoard]);

  return (
    <React.Fragment>
      <h2>BoardsEdit</h2>
    </React.Fragment>
  );
};

const mapStateToProps = (state, ownProps) => {
  return { item: state.boards[ownProps.match.paramas.id] };
};

export default connect(mapStateToProps, { fetchBoard, updateBoard })(
  BoardsEdit
);
